rray<br \\/>\\n*\\/<br \\/>\\nfunction toLayer($cate, $name = \'children\', $pid = 0)<br \\/>\\n{<\\/p>\\n<pre><code>$arr = array();\\nforeach ($cate as $v) {\\n    if (isset($v[\'parentid\'])) {\\n        if ($v[\'parentid\'] == $pid) {\\n            $list = toLayer($cate, $name, $v[\'_id\']);\\n            if (count($list) &gt; 0) {\\n                $v[$name] = $list;\\n            }\\n            $arr[] = $v;\\n        }\\n    } else {\\n        $v[\\"parentid\\"] = 0;\\n        $v[$name] = [];\\n        $arr[] = $v;\\n    }\\n\\n}\\n\\nreturn $arr;<\\/code><\\/pre>\\n<p>}<br \\/>\\n}<\\/p>\\n<\\/li>\\n<\\/ul>\\n<p>if (!function_exists(\'arrays_sorts\')) {<br \\/>\\n\\/**<\\/p>\\n<ul>\\n<li>\\u4e8c\\u7ef4\\u6570\\u7ec4\\u901a\\u8fc7\\u67d0\\u4e2a\\u4e0b\\u6807\\u6392\\u5e8f<\\/li>\\n<li>\\n<\\/li>\\n<li>@param array $arr_info<\\/li>\\n<li>@param $key<\\/li>\\n<li>@return array<br \\/>\\n*\\/<br \\/>\\nfunction arrays_sorts(array $arr_info, $key, $sorts = SORT_ASC)<br \\/>\\n{<br \\/>\\n$sort = array_column($arr_info, $key);<br \\/>\\narray_multisort($sort, $sorts, $arr_info);<br \\/>\\nreturn $arr_info;<br \\/>\\n}<br \\/>\\n}<\\/li>\\n<\\/ul>\\n<p>if (!function_exists(\'object2array\')) {<br \\/>\\n\\/**<\\/p>\\n<ul>\\n<li>\\u5bf9\\u8c61\\u8f6c\\u6210\\u6570\\u7ec4<\\/li>\\n<li>\\n<\\/li>\\n<li>@param $object<\\/li>\\n<li>@return mixed<br \\/>\\n*\\/<br \\/>\\nfunction object2array(&amp;$object)<br \\/>\\n{<br \\/>\\n$object = json_decode(json_encode($object), true);<br \\/>\\nreturn $object;<br \\/>\\n}<br \\/>\\n}<\\/li>\\n<\\/ul>\\n<p>if (!function_exists(\'getMonth\')) {<br \\/>\\nfunction getMonth($sign = &quot;1&quot;)<br \\/>\\n{<br \\/>\\n\\/\\/\\u5f97\\u5230\\u7cfb\\u7edf\\u7684\\u5e74\\u6708<br \\/>\\n$tmp_date = date(&quot;Ym&quot;);<br \\/>\\n\\/\\/\\u5207\\u5272\\u51fa\\u5e74\\u4efd<br \\/>\\n$tmp_year = substr($tmp_date, 0, 4);<br \\/>\\n\\/\\/\\u5207\\u5272\\u51fa\\u6708\\u4efd<br \\/>\\n$tmp_mon = substr($tmp_date, 4, 2);<br \\/>\\n$tmp_nextmont